import { Component } from '@angular/core';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html'
})
export class ContactComponent {
  public valorBitcoin: number = 0.00000000;
  public urlImage: string = "https://bitcoinschool.net.br/assets/images/logo-bitcoin-school-144x68.png"
  public criptomoeda: string = "";

  addSatoshi()
  {
     this.valorBitcoin++;
  }

  delSatoshi()
  {
    this.valorBitcoin = 0.00000000;
  }

  KeyUp(event: any)
  {
    this.criptomoeda = event.target.value;
  }

}

<div class="container-fluid p-1 text-black text-center">
  <h2>The Bitcoin School</h2>
  <img src="https://bitcoinschool.net.br/assets/images/logo-bitcoin-school-144x68.png"/>
  <p>https://bitcoinschool.net.br/</p>

  <p [ngClass]="{
  'text-secondary': valorBitcoin < 0.00000010,
  'text-success': valorBitcoin >= 0.00000010,
  'text-warning': valorBitcoin >= 0.00000020,
  'text-danger': valorBitcoin >= 0.00000020
   }"> Quantidade de Satoshis é {{valorBitcoin}}</p>



<h3>Interpolation</h3>
<h3>Valor bitcoin: {{valorBitcoin}}</h3>

<h3>Property Binding</h3>
<button class="btn btn-warning" [disabled]="valorBitcoin >= 1.00000000">Buy</button><br>
<img [src]="urlImage" height="50px" width="50px" /><br>

<h3> Event Binding</h3>
<button class="btn btn-warning" (click)='addSatoshi()'>Add</button>
<button class="btn btn-warning" (click)='delSatoshi() [disabled]="valorBitcoin < 0.00000010"' >Del</button><br>

<h3>Two-way Binding</h3>

<input type="text" (keyup)='KeyUp($event)' placeholder="Digite a criptomoeda"><br>
<label>A criptomoeda é : {{criptomoeda}}</label>


<input type="text" [ngModel]="criptomoeda" (ngModelChange)='criptomoeda = $event' placeholder="Digite a criptomoeda"><br>
<label>A criptomoeda é : {{criptomoeda}}</label>

<input type="text" [(ngModel)]="criptomoeda" placeholder="Digite a criptomoeda"><br>
<label class="{{criptomoeda}}">A criptomoeda é : {{criptomoeda}}</label>

</div>
